// Find the latest newsletter-combined file from S3 listing with date mismatch detection
// n8n S3 node returns each file as a separate item, not an array
const allInputs = $input.all();

// Get today's date in YYYY-MM-DD format
const today = new Date().toISOString().split('T')[0];

// Debug: log the actual input structure
console.log('Number of input items:', allInputs.length);
console.log('First item:', JSON.stringify(allInputs[0]?.json, null, 2));
console.log('Today\'s date:', today);

// Filter for newsletter-combined files and extract dates
const newsletterFiles = [];

// Process each input item (each represents one file from S3)
for (const inputItem of allInputs) {
  const file = inputItem.json;
  const fileName = file.Key || file.key || file.name || file.fileName;

  if (fileName && fileName.startsWith('newsletter-combined-') && fileName.endsWith('.md')) {
    // Extract date from filename: newsletter-combined-YYYY-MM-DD.md
    const dateMatch = fileName.match(/newsletter-combined-(\d{4}-\d{2}-\d{2})\.md$/);
    if (dateMatch) {
      newsletterFiles.push({
        fileName: fileName,
        date: dateMatch[1],
        lastModified: file.LastModified || file.lastModified || new Date().toISOString()
      });
    }
  }
}

if (newsletterFiles.length === 0) {
  const allFileNames = allInputs.map(item => {
    const file = item.json;
    return file.Key || file.key || file.name || file.fileName || 'unknown';
  });
  throw new Error(`No newsletter-combined files found in R2 bucket. Found ${allInputs.length} total files: ${allFileNames.join(', ')}`);
}

// Sort by date (most recent first)
newsletterFiles.sort((a, b) => new Date(b.date) - new Date(a.date));

const latestFile = newsletterFiles[0];

// Check for date mismatch
const dateMismatch = latestFile.date !== today;
let mismatchWarning = null;
let mismatchSeverity = 'none';

if (dateMismatch) {
  const latestDate = new Date(latestFile.date);
  const todayDate = new Date(today);
  const daysDiff = Math.round((todayDate - latestDate) / (1000 * 60 * 60 * 24));

  if (daysDiff === 1) {
    mismatchSeverity = 'minor';
    mismatchWarning = `Processing yesterday's newsletter (${latestFile.date}) instead of today's (${today}). This might be expected if today's newsletter hasn't been generated yet.`;
  } else if (daysDiff > 1) {
    mismatchSeverity = 'major';
    mismatchWarning = `WARNING: Processing ${daysDiff}-day-old newsletter (${latestFile.date}) instead of today's (${today}). Today's newsletter may be missing from R2 bucket.`;
  } else {
    mismatchSeverity = 'future';
    mismatchWarning = `NOTICE: Processing future-dated newsletter (${latestFile.date}) vs today (${today}). This is unusual but may be intentional.`;
  }
}

return [{
  json: {
    latest_file_key: latestFile.fileName,
    latest_date: latestFile.date,
    latest_modified: latestFile.lastModified,
    total_newsletter_files_found: newsletterFiles.length,
    all_files: newsletterFiles.map(f => ({ fileName: f.fileName, date: f.date })),
    // Date mismatch detection
    workflow_run_date: today,
    date_mismatch: dateMismatch,
    mismatch_severity: mismatchSeverity,
    mismatch_warning: mismatchWarning,
    debug_info: {
      total_files_in_bucket: allInputs.length,
      all_file_keys: allInputs.map(item => {
        const file = item.json;
        return file.Key || file.key || file.name || file.fileName;
      })
    }
  }
}];

// Configuration and initialization - DYNAMIC DATE DETECTION
// This will be populated by the previous "Find Latest Newsletter" node
const latestFileInfo = $input.first().json;
const processingDate = latestFileInfo.latest_date;
const combinedMarkdownKey = latestFileInfo.latest_file_key;

// Newsletter context for AI agents
const newsletterContext = {
  target_date: processingDate,
  audience_profile: {
    primary_segments: ['ai_professionals', 'tech_entrepreneurs', 'developers'],
    interests: ['ai_breakthroughs', 'practical_applications', 'industry_news', 'research_insights'],
    preferred_tone: 'professional_but_accessible',
    max_articles_per_newsletter: 4
  },
  editorial_guidelines: {
    min_quality_score: 7.0,
    max_duplicate_similarity: 0.85,
    required_diversity: ['topic', 'source', 'perspective'],
    preferred_mix: {
      breaking_news: 0.25,
      analysis: 0.5,
      research: 0.25
    }
  }
};

return [{
  json: {
    combined_markdown_key: combinedMarkdownKey,
    processing_date: processingDate,
    newsletter_context: newsletterContext
  }
}];

// Parse combined markdown into individual articles
const context = $('Initialize AI Context').first().json;
let markdownContent = '';

// Extract markdown content from S3 response
if ($input.first().binary && $input.first().binary.data) {
  try {
    let binaryContent;
    if (typeof $input.first().binary.data === 'string') {
      binaryContent = JSON.parse($input.first().binary.data);
    } else {
      binaryContent = $input.first().binary.data;
    }

    if (binaryContent && binaryContent.data) {
      markdownContent = Buffer.from(binaryContent.data, 'base64').toString('utf8');
    }
  } catch (error) {
    markdownContent = $input.first().binary.data.toString('utf8');
  }
}

// Parse markdown into individual articles
const articles = [];
const lines = markdownContent.split('\n');
let currentArticle = null;
let currentContent = [];

for (const line of lines) {
  // Detect article headers: ### Article Title (Source Name)
  const articleMatch = line.match(/^### (.+) \((.+)\)$/);
  if (articleMatch) {
    // Save previous article if exists
    if (currentArticle) {
      currentArticle.content = currentContent.join('\n').trim();
      currentArticle.word_count = currentArticle.content.split(/\s+/).filter(w => w.length > 0).length;
      articles.push(currentArticle);
    }

    // Start new article
    currentArticle = {
      article_id: `article_${context.processing_date}_${articles.length + 1}`,
      title: articleMatch[1].trim(),
      source: articleMatch[2].trim().toLowerCase().replace(/\s+/g, '-'),
      date: context.processing_date,
      content: '',
      word_count: 0
    };
    currentContent = [];
  } else if (line === '---') {
    // Article separator - ignore
    continue;
  } else if (line.match(/^## \d{4}-\d{2}-\d{2}$/)) {
    // Date header - extract date for subsequent articles
    const dateMatch = line.match(/^## (\d{4}-\d{2}-\d{2})$/);
    if (dateMatch && currentArticle) {
      currentArticle.date = dateMatch[1];
    }
  } else if (currentArticle && line.trim()) {
    // Article content line
    currentContent.push(line);
  }
}

// Don't forget the last article
if (currentArticle) {
  currentArticle.content = currentContent.join('\n').trim();
  currentArticle.word_count = currentArticle.content.split(/\s+/).filter(w => w.length > 0).length;
  articles.push(currentArticle);
}

// Return context with parsed articles
return [{
  json: {
    ...context,
    articles: articles,
    total_articles: articles.length,
    processing_stage: 'articles_parsed'
  }
}];

// Simple deduplication logic without AI tools
const context = $input.first().json;
const articles = context.articles || [];

// Simple deduplication based on title similarity and same date
function calculateTitleSimilarity(title1, title2) {
  const words1 = title1.toLowerCase().split(/\s+/).filter(w => w.length > 3);
  const words2 = title2.toLowerCase().split(/\s+/).filter(w => w.length > 3);

  const intersection = words1.filter(w => words2.includes(w)).length;
  const union = new Set([...words1, ...words2]).size;

  return union > 0 ? intersection / union : 0;
}

// Analyze articles for duplicates
const deduplicated = [];
const removed = [];
const processed = new Set();

for (let i = 0; i < articles.length; i++) {
  if (processed.has(i)) continue;

  const article1 = articles[i];
  let isDuplicate = false;
  let duplicateOf = null;

  // Check against all other articles
  for (let j = i + 1; j < articles.length; j++) {
    if (processed.has(j)) continue;

    const article2 = articles[j];
    const titleSim = calculateTitleSimilarity(article1.title, article2.title);
    const sameDate = article1.date === article2.date;

    // Simple duplicate detection: high title similarity + same date
    if (titleSim > 0.7 && sameDate) {
      // Keep the one with more content
      if (article2.word_count > article1.word_count) {
        removed.push({
          article_id: article1.article_id,
          removal_reason: `Duplicate of ${article2.article_id} (title similarity: ${titleSim.toFixed(2)})`,
          duplicate_of: article2.article_id
        });
        isDuplicate = true;
        duplicateOf = article2.article_id;
        processed.add(i);
        break;
      } else {
        removed.push({
          article_id: article2.article_id,
          removal_reason: `Duplicate of ${article1.article_id} (title similarity: ${titleSim.toFixed(2)})`,
          duplicate_of: article1.article_id
        });
        processed.add(j);
      }
    }
  }

  if (!isDuplicate) {
    // Add authority score based on source
    const authorityScores = {
      'techcrunch': 9,
      'the-verge': 8,
      'wired': 8,
      'ars-technica': 8,
      'reuters': 9,
      'bloomberg': 9,
      'futurepedia': 6,
      'superhuman': 6,
      'taaft': 6,
      'the-neuron': 6
    };

    deduplicated.push({
      ...article1,
      deduplication_status: 'kept_primary',
      reasoning: 'No duplicates found with sufficient similarity',
      similar_to: [],
      authority_score: authorityScores[article1.source] || 5,
      tool_analysis: {
        temporal_relationship: 'separate_events',
        semantic_similarity: 0,
        confidence: 'simple_algorithm'
      }
    });
  }
}

const result = {
  deduplicated_articles: deduplicated,
  removed_articles: removed,
  agent_reasoning: [
    `Processed ${articles.length} articles using simple title similarity algorithm`,
    `Removed ${removed.length} duplicates with >70% title similarity on same date`,
    `Kept ${deduplicated.length} unique articles for further processing`
  ]
};

return [{
  json: result
}];

// Simple quality evaluation without AI tools
const dedupResult = $input.first().json;
const articles = dedupResult.deduplicated_articles || [];

// Simple quality scoring
function evaluateArticle(article) {
  let qualityScore = 5; // Base score
  let relevanceScore = 5;
  let factualScore = 7; // Assume good unless proven otherwise

  // Content length scoring
  if (article.word_count > 500) qualityScore += 2;
  else if (article.word_count > 200) qualityScore += 1;
  else if (article.word_count < 50) qualityScore -= 2;

  // Source authority
  const sourceBonus = article.authority_score > 7 ? 2 : article.authority_score > 5 ? 1 : 0;
  qualityScore += sourceBonus;

  // AI/Tech relevance keywords
  const content = (article.title + ' ' + article.content).toLowerCase();
  const aiKeywords = ['ai', 'artificial intelligence', 'machine learning', 'gpt', 'claude', 'openai', 'anthropic', 'model', 'neural', 'automation'];
  const techKeywords = ['tech', 'technology', 'innovation', 'startup', 'funding', 'breakthrough', 'research', 'development'];

  const aiMatches = aiKeywords.filter(kw => content.includes(kw)).length;
  const techMatches = techKeywords.filter(kw => content.includes(kw)).length;

  if (aiMatches > 2) relevanceScore += 3;
  else if (aiMatches > 0) relevanceScore += 2;
  else if (techMatches > 1) relevanceScore += 1;

  // Composite score
  const compositeScore = (qualityScore * 0.4 + relevanceScore * 0.4 + factualScore * 0.2);

  // Topic classification
  let primaryTopic = 'general_tech';
  if (aiMatches > 0) primaryTopic = 'artificial_intelligence';
  else if (content.includes('funding') || content.includes('investment')) primaryTopic = 'funding';
  else if (content.includes('research') || content.includes('study')) primaryTopic = 'research';

  return {
    ...article,
    quality_score: Math.min(10, Math.max(1, qualityScore)),
    relevance_score: Math.min(10, Math.max(1, relevanceScore)),
    factual_score: factualScore,
    composite_score: Math.min(10, Math.max(1, compositeScore)),
    primary_topic: primaryTopic,
    recommendation: compositeScore >= 7 ? 'include' : 'exclude',
    reasoning: `Quality: ${qualityScore.toFixed(1)}, Relevance: ${relevanceScore.toFixed(1)}, Authority: ${article.authority_score}`,
    audience_segments: aiMatches > 0 ? ['ai_professionals', 'developers'] : ['tech_entrepreneurs'],
    impact_factors: aiMatches > 2 ? ['ai_advancement', 'industry_impact'] : ['general_interest']
  };
}

const evaluatedArticles = articles.map(evaluateArticle);
const aboveThreshold = evaluatedArticles.filter(a => a.composite_score >= 7).length;
const belowThreshold = evaluatedArticles.filter(a => a.composite_score < 7).length;
const averageScore = evaluatedArticles.reduce((sum, a) => sum + a.composite_score, 0) / evaluatedArticles.length;

const result = {
  evaluated_articles: evaluatedArticles,
  quality_statistics: {
    above_threshold: aboveThreshold,
    below_threshold: belowThreshold,
    average_score: averageScore
  },
  agent_reasoning: [
    `Evaluated ${evaluatedArticles.length} articles using simple scoring algorithm`,
    `${aboveThreshold} articles meet quality threshold (7.0+)`,
    `Average quality score: ${averageScore.toFixed(1)}`
  ]
};

return [{
  json: result
}];

// Simple curation without AI tools
const qualityResult = $input.first().json;
const articles = qualityResult.evaluated_articles.filter(a => a.recommendation === 'include') || [];

// Sort by composite score and select top 4
const sortedArticles = articles.sort((a, b) => b.composite_score - a.composite_score);
const maxArticles = 4;
const selected = sortedArticles.slice(0, maxArticles);
const rejected = sortedArticles.slice(maxArticles);

// Create curated selection
const curatedArticles = selected.map((article, index) => ({
  article_id: article.article_id,
  selection_rank: index + 1,
  curation_score: article.composite_score,
  selection_reasoning: `Ranked #${index + 1} with composite score ${article.composite_score.toFixed(1)}. ${article.reasoning}`,
  impact_assessment: {
    significance: article.composite_score > 8.5 ? 'high' : article.composite_score > 7.5 ? 'medium' : 'low',
    factors: article.impact_factors
  }
}));

const rejectedArticles = rejected.map(article => ({
  article_id: article.article_id,
  rejection_reason: `Lower priority: composite score ${article.composite_score.toFixed(1)} (rank ${selected.length + rejected.indexOf(article) + 1})`
}));

// Portfolio analysis
const topicCounts = {};
const sourceCounts = {};

selected.forEach(article => {
  topicCounts[article.primary_topic] = (topicCounts[article.primary_topic] || 0) + 1;
  sourceCounts[article.source] = (sourceCounts[article.source] || 0) + 1;
});

const diversityScore = Object.keys(sourceCounts).length * 2.5; // Max 10 for 4 different sources

const result = {
  curated_articles: curatedArticles,
  rejected_articles: rejectedArticles,
  portfolio_analysis: {
    diversity_score: Math.min(10, diversityScore),
    topic_balance: topicCounts,
    source_distribution: sourceCounts,
    narrative_flow: 'Selected top-scoring articles with quality and relevance prioritization'
  },
  agent_reasoning: [
    `Selected top ${selected.length} articles from ${articles.length} quality candidates`,
    `Portfolio diversity score: ${diversityScore.toFixed(1)}/10`,
    `Topic distribution: ${Object.keys(topicCounts).join(', ')}`
  ]
};

return [{
  json: result
}];

// Combine all agent outputs into final curation results
const latestFileInfo = $('Find Latest Newsletter').first().json;
const initialContext = $('Initialize AI Context').first().json;
const parsedData = $('Parse Individual Articles').first().json;
const deduplicationResult = $('Simple Deduplication').first().json;
const evaluationResult = $('Simple Quality Evaluation').first().json;
const curationResult = $('Simple Story Curation').first().json;

// Merge article data with agent analysis
const finalArticles = curationResult.curated_articles.map(curatedArticle => {
  // Find original article data
  const originalArticle = parsedData.articles.find(a => a.article_id === curatedArticle.article_id);

  // Find deduplication analysis
  const dedupAnalysis = deduplicationResult.deduplicated_articles.find(a => a.article_id === curatedArticle.article_id);

  // Find evaluation analysis
  const evalAnalysis = evaluationResult.evaluated_articles.find(a => a.article_id === curatedArticle.article_id);

  return {
    ...originalArticle,
    agent_metadata: {
      deduplication: {
        status: dedupAnalysis?.deduplication_status || 'unknown',
        reasoning: dedupAnalysis?.reasoning || 'No deduplication analysis',
        authority_score: dedupAnalysis?.authority_score || 0
      },
      quality_evaluation: {
        composite_score: evalAnalysis?.composite_score || 0,
        quality_score: evalAnalysis?.quality_score || 0,
        relevance_score: evalAnalysis?.relevance_score || 0,
        primary_topic: evalAnalysis?.primary_topic || 'unknown',
        recommendation: evalAnalysis?.recommendation || 'unknown',
        reasoning: evalAnalysis?.reasoning || 'No evaluation analysis'
      },
      curation_decision: {
        selection_rank: curatedArticle.selection_rank,
        curation_score: curatedArticle.curation_score,
        reasoning: curatedArticle.selection_reasoning,
        impact_assessment: curatedArticle.impact_assessment
      }
    }
  };
});

// Create comprehensive results
const finalResults = {
  processing_metadata: {
    processing_date: initialContext.processing_date,
    workflow_version: '2.1_simple_algorithms_with_date_validation',
    total_articles_processed: parsedData.total_articles,
    agents_used: ['simple_deduplication', 'simple_quality_evaluation', 'simple_story_curation'],
    // Date validation information
    workflow_run_date: latestFileInfo.workflow_run_date,
    date_mismatch: latestFileInfo.date_mismatch,
    mismatch_severity: latestFileInfo.mismatch_severity,
    mismatch_warning: latestFileInfo.mismatch_warning,
    processed_file: latestFileInfo.latest_file_key
  },
  selected_articles: finalArticles,
  curation_analysis: {
    portfolio_analysis: curationResult.portfolio_analysis,
    quality_statistics: evaluationResult.quality_statistics
  },
  agent_processing_log: {
    deduplication_agent: {
      removed_count: deduplicationResult.removed_articles?.length || 0,
      reasoning: deduplicationResult.agent_reasoning || []
    },
    content_evaluator_agent: {
      statistics: evaluationResult.quality_statistics,
      reasoning: evaluationResult.agent_reasoning || []
    },
    story_curator_agent: {
      final_selections: curationResult.curated_articles?.length || 0,
      reasoning: curationResult.agent_reasoning || []
    }
  }
};

return [{ json: finalResults }];

