// Assemble the complete newsletter from aggregated story segments
// The current input ($json) contains the aggregated story content
const aggregatedStories = $json;

// Retrieve data from previous nodes using node references
const subjectLineData = $('Generate Subject Lines').item.json.output;
const introData = $('Generate Newsletter Intro').item.json.output;
const metadata = $('Parse Curated Data').item.json.metadata;
const processingInfo = $('Find Latest Curated File').item.json;

// Build newsletter content with proper formatting
// Fix the Plus: formatting in pre-header text
let preHeaderText = subjectLineData.preHeaderText;
if (preHeaderText.includes('Plus:')) {
  preHeaderText = preHeaderText.replace(/Plus:/g, '**Plus:**');
}

const newsletter = `# ${subjectLineData.primarySubjectLine}

${preHeaderText}

---

${introData.introContent}

---

${aggregatedStories.storyContent.join('\n\n---\n\n')}

---

*Generated from curated stories on ${processingInfo.processingDate}*
*Source: ${processingInfo.latestFile.fileName}*
*Stories processed: ${metadata.totalArticles} | Average score: ${metadata.averageScore}*`;

// Prepare output data
const outputFileName = `newsletter-${processingInfo.latestFile.date}.md`;
const newsletterData = {
  content: newsletter,
  fileName: outputFileName,
  generatedDate: new Date().toISOString(),
  sourceFile: processingInfo.latestFile.fileName,
  sourceDate: processingInfo.latestFile.date,
  subjectLine: subjectLineData.primarySubjectLine,
  preHeaderText: subjectLineData.preHeaderText,
  alternativeSubjects: subjectLineData.alternativeSubjectLines,
  storiesCount: aggregatedStories.storyContent.length,
  dateMismatch: processingInfo.dateMismatch,
  mismatchSeverity: processingInfo.mismatchSeverity,
  metadata: metadata
};

return newsletterData;

