// Find latest curated-stories file
const curatedFiles = [];

for (const inputItem of $input.all()) {
  const file = inputItem.json;
  const fileName = file.Key || file.key || file.name || file.fileName;

  if (fileName && fileName.startsWith('curated-stories-') && fileName.endsWith('.json')) {
    const dateMatch = fileName.match(/curated-stories-(\d{4}-\d{2}-\d{2})\.json$/);
    if (dateMatch) {
      curatedFiles.push({
        fileName: fileName,
        date: dateMatch[1],
        lastModified: file.LastModified || file.lastModified || new Date().toISOString(),
        fullPath: fileName
      });
    }
  }
}

// Sort by date (newest first)
curatedFiles.sort((a, b) => new Date(b.date) - new Date(a.date));

if (curatedFiles.length === 0) {
  throw new Error('No curated-stories files found in R2 bucket');
}

// Get the latest file
const latestFile = curatedFiles[0];
const today = new Date().toISOString().split('T')[0];
const todayDate = new Date(today);
const latestDate = new Date(latestFile.date);

// Date validation (non-interrupting)
const dateMismatch = latestFile.date !== today;
let mismatchSeverity = 'none';
if (dateMismatch) {
  const daysDiff = Math.round((todayDate - latestDate) / (1000 * 60 * 60 * 24));
  if (daysDiff === 1) mismatchSeverity = 'minor';
  else if (daysDiff > 1) mismatchSeverity = 'major';
  else mismatchSeverity = 'future';
}

return {
  latestFile: latestFile,
  dateMismatch: dateMismatch,
  mismatchSeverity: mismatchSeverity,
  allFiles: curatedFiles,
  processingDate: today
};

// Parse the curated stories JSON with enhanced error handling
let jsonContent;

try {
  // Get the data from the extract node
  const rawData = $input.first().json.data;

  if (!rawData) {
    throw new Error('No data received from extract node');
  }

  // Parse the JSON content
  jsonContent = typeof rawData === 'string' ? JSON.parse(rawData) : rawData;

  console.log('Parsed JSON structure keys:', Object.keys(jsonContent));

} catch (parseError) {
  throw new Error(`Failed to parse JSON content: ${parseError.message}`);
}

// Validate structure
if (!jsonContent) {
  throw new Error('Empty JSON content after parsing');
}

// Handle both selectedStories (sample format) and selected_articles (actual format)
let storiesArray;
if (jsonContent.selectedStories) {
  storiesArray = jsonContent.selectedStories;
} else if (jsonContent.selected_articles) {
  storiesArray = jsonContent.selected_articles;
} else {
  throw new Error(`Invalid curated-stories JSON structure: missing stories array. Available properties: ${Object.keys(jsonContent).join(', ')}`);
}

if (!Array.isArray(storiesArray)) {
  throw new Error(`Stories data is not an array, it is: ${typeof storiesArray}`);
}

if (storiesArray.length === 0) {
  throw new Error('No stories found in stories array');
}

// Extract metadata - handle both formats
const metadata = {
  processedDate: jsonContent.processing_metadata?.processing_date || jsonContent.processedDate || jsonContent.date || new Date().toISOString(),
  totalArticles: jsonContent.processing_metadata?.total_articles_processed || jsonContent.totalArticles || storiesArray.length,
  averageScore: jsonContent.curation_analysis?.quality_statistics?.average_score || jsonContent.averageScore || 0,
  diversityScore: jsonContent.curation_analysis?.portfolio_analysis?.diversity_score || jsonContent.diversityScore || 0,
  source: 'curated-stories',
  workflowVersion: jsonContent.processing_metadata?.workflow_version || jsonContent.metadata?.workflowVersion || 'unknown'
};

// Extract the top 4 stories for newsletter
const stories = storiesArray.slice(0, 4).map((story, index) => ({
  ...story,
  position: index + 1,
  isLead: index === 0
}));

console.log(`Successfully parsed ${stories.length} stories for newsletter`);
console.log('Story titles:', stories.map(s => s.title));

return {
  stories: stories,
  metadata: metadata,
  originalData: jsonContent
};

